export default function Todo({ todo }) {
    // Access the status from the todo object or default to false if not present
    const status = todo.status || false;
  
    return (
      <div className="todo">
        <p>{todo.todo}</p>
        <div>
          <button className="todo__status">
            {status ? "☑" : "☐"} {/* Display checked or unchecked */}
          </button>
        </div>
      </div>
    );
  }
